import React from 'react';
import { Link } from 'react-scroll';
import styled from 'styled-components';
import Logo from '../assets/images/logo-2.png';
import ParagraphText from './paragraphTexts/ParagraphText';

const FooterStyles = styled.footer`
  background-color: var(--darkBlue_4);
  padding: 10rem 0;
  .footer__wrapper {
    text-align: center;
  }
  .img_footer {
    max-width: 200px;
    margin: 0 auto;
    margin-bottom: 1rem;
  }
  .footer__desc {
    color: var(--white);
    max-width: 350px;
    margin: 0 auto;
    margin-bottom: 2rem;
  }
  .footer__links {
    margin-bottom: 2rem;
    li {
      display: inline-block;
      margin: 0 1rem;
    }
    a {
      font-size: 1.6rem;
      line-height: 1.5em;
      color: var(--lightBlue_1);
    }
    li:hover {
      a {
        color: var(--mediumSlateBlue);
        text-decoration: underline;
      }
    }
  }
  .footer__copyright {
    font-size: 1.2rem;
    color: var(--lightBlue_1);
  }
  @media only screen and (max-width: 768px) {
    .footer__links {
      a {
        font-size: 1.4rem;
      }
    }
  }
`;

function Footer() {
  return (
    <FooterStyles>
      <div className="container">
        <div className="footer__wrapper">
          <div className="img_footer">
            <img src={Logo} alt="Savio" />
          </div>
          <ParagraphText className="footer__desc">
            Deixe-me elevar seu vídeo a um novo patamar.
          </ParagraphText>
          <div className="footer__links">
            <ul>
              <li>
                <Link to="home" smooth>
                  Home
                </Link>
              </li>
              <li>
                <Link to="services" smooth>
                  Serviços
                </Link>
              </li>
              <li>
                <Link to="about" smooth>
                  Sobre
                </Link>
              </li>
              <li>
                <Link to="contact" smooth>
                  Contato
                </Link>
              </li>
            </ul>
          </div>
          <ParagraphText className="footer__copyright">
            ©{' '}
            <a
              href="https://lucasgomesdev.netlify.app/"
              target="_blank"
              rel="noopener noreferrer"
            >
              Dev Log - Lucas Gomes{' '}
            </a>
            {new Date().getFullYear()}. Todos os direitos reservados.
          </ParagraphText>
        </div>
      </div>
    </FooterStyles>
  );
}

export default Footer;
